import Tkinter as tk
from Tkinter import PhotoImage


class Title_bar(tk.Frame):

    def __init__(self, master=None):
        self.root = master
        tk.Frame.__init__(self, master)
        self.pack()
        self._create_widgets()

    def close_button(self):
        self.root.destroy()
        
    # Adds all the widgets.
    def _create_widgets(self):
        # Main window
        main_window = tk.Frame(self)
        main_window.pack(side="right")
        min_button = tk.Button(main_window, text="Min")
        max_button = tk.Button(main_window, text="Max")
        close_button = tk.Button(main_window, text="X", command=self.close_button)
                #Specify location
        min_button.grid(row = 0, column = 0)
        max_button.grid(row = 0, column = 1)
        close_button.grid(row = 0, column = 2)

def add_custom_title_bar(parent):
     frame = tk.Frame(parent)
#     frame.pack() 
     title_bar_frame = Title_bar(master = frame)
     title_bar_frame.pack(side = "top")
     return frame
     
if __name__ == "__main__":
    root = tk.Tk()
    app = Title_bar(master=root)
    root.geometry("300x50+150+150")
    icon = PhotoImage(
        data="")
    root.tk.call('wm', 'iconphoto', root._w, icon)
    app.mainloop()
